import boto3

dynamodb = boto3.resource('dynamodb')
table = dynamodb.Table('affective')

def put_visit(user_sub, link_key, event_time, device):
    print('Adding visit to db')
    print(user_sub, link_key, event_time, device)
    # Add to database
    table.put_item(
        Item={
            'pk': f'USER#{user_sub}',
            'sk': link_key,
            'eventTime': event_time,
            'device': device,
        }
    )


